mapper.insert:
INSERT tb_member  
SET mem_name  =    #{mem_name}
 	 , mem_phone = #{mem_phone}
 	 , mem_email =   #{mem_email}
VALUES mem_id =    #{mem_id}

mapper.update:
 UPDATE tb_member   
  SET mem_name  = #{mem_name}
    , mem_phone = #{mem_phone}
    , mem_email = #{mem_email}
VALUES mem_id = #{mem_id}

mapper.delete:
 	DELETE tb_member
	WHERE mem_id = #{mem_id}
	and mem_id = #{mem_id}
